// AngularJS 1.4.5 最小化版本
window.angular = (function() {
    'use strict';
    var angular = {
        version: {full: '1.4.5'},
        module: function(name, requires) {
            console.log('Angular模块创建:', name);
            var moduleInstance = {name: name, requires: requires || []};
            window.angular.modules = window.angular.modules || {};
            window.angular.modules[name] = moduleInstance;
            return moduleInstance;
        }
    };
    return angular;
})();